const openModalButtons=document.querySelectorAll("[data-modal-target]"),closeModalButtons=document.querySelectorAll("[data-close-button]"),overlay=document.getElementById("overlay"),spanCounter=document.querySelector("span.counter"),resetButtons=document.querySelectorAll("[data-reset-button]");let boxClickCount=0;function openModal(t){null!=t&&(t.classList.add("active"),overlay.classList.add("active"))}function closeModal(t){null!=t&&(t.classList.remove("active"),overlay.classList.remove("active"))}function reset(){5<boxClickCount&&(boxClickCount=0,spanCounter.textContent=boxClickCount,localStorage.setItem("boxClickCountStorage",boxClickCount))}null!=localStorage.getItem("boxClickCountStorage")&&(boxClickCount=Number(localStorage.getItem("boxClickCountStorage"))),overlay.addEventListener("click",()=>{const t=document.querySelectorAll(".modal.active");t.forEach(t=>{closeModal(t)})}),openModalButtons.forEach(o=>{o.addEventListener("click",()=>{const t=document.querySelector(o.dataset.modalTarget);if(openModal(t),boxClickCount+=1,5<(spanCounter.textContent=boxClickCount)){const e=t.querySelector("[data-reset-button]");e.classList.add("active")}localStorage.setItem("boxClickCountStorage",boxClickCount)})}),closeModalButtons.forEach(t=>{t.addEventListener("click",()=>{closeModal(t.closest(".modal.active"))})}),resetButtons.forEach(t=>{t.addEventListener("click",()=>{reset(),t.classList.remove("active")})});